import java.util.Scanner;


public class Q10411_BackToHighSchoolPhysics 
{

	/**
	 * @param args
	 */
	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		Scanner scn = new Scanner(System.in);
		while(scn.hasNext())
		{
			int v = scn.nextInt();
			int t = scn.nextInt();
			System.out.println(2*v*t);
		}
	}

}
